    def __define_atomic_number_dictionary (self):
        """ Function doc """
        self.elements_by_number = {}
        
        for symbol in self.elements_by_symbol.keys():
            atomic_num = self.elements_by_symbol[symbol][0]
            self.elements_by_number[atomic_num] = symbol
        
    def get_color_palette (self, custom = {"C":[0.0 ,0.5 ,0.0]}):
        """ Function doc """
        color_palette = {}
        for symbol in self.elements_by_symbol.keys():
            #print(self.elements_by_symbol[symbol])
            #RGB = self.elements_by_symbol[symbol]['RGB Color Indices']
            if symbol in custom.keys():
                color_palette [symbol] = custom[symbol]
            else:
                color_palette [symbol] = self.elements_by_symbol[symbol][2]
        return color_palette

    def get_atomic_mass (self, symbol = None, number = None):
        """ Function doc """ 
        symbol = self.check_symbol (symbol, number)
        return self.elements_by_symbol[symbol][3]
    
    def get_vdw_radius (self, symbol = None , number = None):
        """ Function doc """
        symbol = self.check_symbol (symbol, number)
        return  self.elements_by_symbol[symbol][6]
    
    def get_cov_radius (self, symbol = None, number = None):
        """ Function doc """
        symbol = self.check_symbol (symbol, number)
        return  self.elements_by_symbol[symbol][5]
    
    def get_disp_radius (self, symbol = None):
        """ Function doc """
        symbol = self.check_symbol (symbol, number)
        return  self.elements_by_symbol[symbol][4]
        
    def get_element_list (self):
        """ Function doc """
        return self.elements_by_symbol.keys()
        
    def check_symbol (self, symbol, number):
        """ Function doc """
        if symbol == None:  
            symbol = self.elements_by_number[number]
        else:
            pass
        return symbol











